import React, { useState } from 'react';
import { getUser } from '../../api/axiosConfig'; // Update with the actual path
import { userLoginAsStudent } from '../../api/axiosConfig'; // Import the userLogin function
import { useNavigate} from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import logo from '../assets/logo.png';


const EditProfilePage = ({ user, updateUser }) => {
    const [userData, setUserData] = useState(null);
    const { userId } = useParams();
    const navigate = useNavigate();

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [password2, setPassword2] = useState('');
    const [bio, setBio] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [surname, setSurname] = useState('');

    useEffect(() => {
        getUser(userId)
          .then(response => {
            setUserData(response.data);
          })
          .catch(error => {
            console.error('There was an error fetching the user data:', error);
          });
      }, [userId]);
    /**
    const handleEdit = async (event) => {
        event.preventDefault();

        //checks if it is a bilkent email
        if (!email.endsWith('@ug.bilkent.edu.tr') && !email.endsWith('@alumni.bilkent.edu.tr')) {
          alert('Please use a Bilkent email address');
          return;
        }
        //checks if the confirm password is correct
        if (password !== repassword) {
            alert("Passwords do not match!");
            return;
        }
        // Registering the user 
        try {
            const response = await userRegister(email, password, name, surname, bio);
            console.log("Registered!!")
            navigate('/verification-page'); // Navigates to verification page for email verification
        } catch (err) {
            setError(err.response?.data?.message || 'An error occurred during registration');
        }
    };
    */
    const handleUpdate = async (event) => {
        event.preventDefault(); // Prevent default form submission
        if(password.length < 8){
            alert("Password must be at least 8 characters long");
            return;
        }
        if(password !== password2){
            alert("Passwords do not match!");
            return;
        }
        if (name === '' || surname === '' || bio === '') {
            alert("Please fill all the fields");
            return;
        }
        try {
            const response = await updateUser(userId, { name, surname, password, password2, bio});
            console.log(response.data);
            setSuccess('Profile updated successfully');
        } catch (err) {
            setError(err.response?.data?.message || 'An error occurred during update');
            // Handle update error here
        }
    };

    return (
        <div className="container">
            <h1 className="m-4">Edit Profile</h1>
            <div className="card" style={{ width: '26rem', margin: 'auto', background: 'radial-gradient(circle, rgba(238,174,202,1) 0%, rgba(212,221,232,1) 100%)' }}>
                <img src={logo} className="card-img-top mt-3" style={{ width: '50%', margin: 'auto' }} alt="Bilkent Uni Logo" />
                <div className="card-body">
                    <form onSubmit={handleUpdate} className="myform">
                        <div className="form-group mt-2">
                            <label htmlFor="name">Name</label>
                            <input
                                id="name"
                                className="form-control"
                                type="text"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                placeholder="Please enter your full name"
                            />
                        </div>

                        <div className="form-group mt-2">
                            <label htmlFor="name">Surname</label>
                            <input
                                id="surname"
                                className="form-control"
                                type="text"
                                value={surname}
                                onChange={(e) => setSurname(e.target.value)}
                                placeholder="Please enter your surname"
                            />
                        </div>

                        <div className="form-group mt-2">
                            <label htmlFor="password">Enter Password</label>
                            <input
                                id="password"
                                className="form-control"
                                type="text"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="Please enter your password"
                            />
                        </div>

                        <div className="form-group mt-2">
                            <label htmlFor="password2">Enter Password Again</label>
                            <input
                                id="password2"
                                className="form-control"
                                type="text"
                                value={password2}
                                onChange={(e) => setPassword2(e.target.value)}
                                placeholder="Please enter your password again"
                            />
                        </div>

                        <div className="form-group mt-2">
                            <label htmlFor="bio">Enter Bio</label>
                            <input
                                id="bio"
                                className="form-control"
                                type="text"
                                value={bio}
                                onChange={(e) => setBio(e.target.value)}
                                placeholder="Please enter your bio"
                            />
                        </div>

                        <div className="form-group mt-2">
                            <button type="submit" className="btn btn-primary">Update</button>
                        </div>
                    </form>
                    {error && <div className="alert alert-danger">{error}</div>}
                    {success && <div className="alert alert-success">{success}</div>}
                </div>
            </div>
        </div>


    );
}

export default EditProfilePage;
