import React, { useState } from 'react';
import { createUserPost } from '../../api/axiosConfig';

const MakePost = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [postType, setPostType] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const userId = 'your-user-id'; // This should be dynamically obtained based on your app's user management

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('content', content);
    formData.append('postType', postType);
    formData.append('category', category);
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await createUserPost(userId, formData); // Update this call based on your backend's requirement
      console.log('Post created:', response.data);
      // Handle success (e.g., show success message, redirect to another page, etc.)
    } catch (error) {
      console.error('Error creating post:', error);
      // Handle error (e.g., show error message)
    }
  };

  return (
    <div className="container">
      <h1 className="mt-4 text-center">Create Post</h1>
      <form onSubmit={handleSubmit}>
        {/* Image Upload */}
        <div>
          <label>Post Image</label>
          <input type="file" onChange={handleImageChange} />
        </div>

        {/* Post Title */}
        <div>
          <label>Post Title</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </div>

        {/* Post Content */}
        <div>
          <label>Post Content</label>
          <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
        </div>

        {/* Post Type */}
        <div>
          <label>Post Type</label>
          <select value={postType} onChange={(e) => setPostType(e.target.value)}>
            <option value="sell">Sell</option>
            <option value="borrow">Borrow</option>
            <option value="lost">Lost</option>
            <option value="found">Found</option>
            <option value="free">Free</option>
          </select>
        </div>

        {/* Category */}
        <div>
          <label>Category</label>
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="books">Books</option>
            <option value="electronics">Electronics</option>
            <option value="dormitory">Dormitory</option>
            {/* Add more categories as needed */}
          </select>
        </div>

        <button type="submit">Create Post</button>
      </form>
    </div>
  );
};

export default MakePost;
