import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { userRegister } from '../../api/axiosConfig';
import '../../themes/styles.css'; // Assuming you have a styles.css file with your custom styles

const RegisterPage = () => {
    const [formData, setFormData] = useState({
        eposta: '',
        parola: '',
        reparola: '',
        ad: '',
        soyad: '',
        bio: ''
    });

    const [verificationCode, setVerificationCode] = useState('');
    const [isRegistered, setIsRegistered] = useState(false);
    const [error, setError] = useState(''); // State to handle any error

    const navigate = useNavigate();

    const handleInputChange = (e) => {
      setFormData({...formData, [e.target.name]: e.target.value});
  };

    const handleRegister = async (event) => {
        event.preventDefault();

        //checks if it is a bilkent email
        if (!formData.eposta.endsWith('@ug.bilkent.edu.tr') && !formData.eposta.endsWith('@alumni.bilkent.edu.tr')) {
          alert('Please use a Bilkent email address');
          return;
        }
        //checks if the confirm password is correct
        if (formData.parola !== formData.reparola) {
            alert("Passwords do not match!");
            return;
        }
        // Registering the user 
        try {
          console.log({ email, password, name, surname, bio });
            await userRegister({
                email: formData.eposta,
                password: formData.parola,
                name: formData.ad,
                surname: formData.soyad,
                bio: formData.bio
            });
            navigate('/verification-page'); // Navigates to verification page for email verification
        } catch (err) {
            setError(err.response?.data?.message || 'An error occurred during registration');
        }
    };

    return (
        <>
            {/* Include your Navbar here as a separate component */}
            <div className="container">
                <form onSubmit={handleRegister}>
                    <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">
                        {/* Other form fields */}
                        <input 
                            type="email" 
                            name="eposta"
                            className="form-control mb-2" 
                            value={formData.eposta} 
                            onChange={handleInputChange} 
                            placeholder="Bilkent Mail" 
                            required 
                        />
                        <input 
                            type="password" 
                            name="parola"
                            className="form-control mb-2" 
                            value={formData.parola} 
                            onChange={handleInputChange} 
                            placeholder="Password" 
                            required 
                        />
                        <input 
                            type="password" 
                            name="reparola"
                            className="form-control mb-2" 
                            value={formData.reparola} 
                            onChange={handleInputChange} 
                            placeholder="Re-type Password" 
                            required 
                        />
                        <input 
                            type="text" 
                            name="ad"
                            className="form-control mb-2" 
                            value={formData.ad} 
                            onChange={handleInputChange} 
                            placeholder="Name" 
                            required 
                        />
                        <input 
                            type="text" 
                            name="soyad"
                            className="form-control mb-2" 
                            value={formData.soyad} 
                            onChange={handleInputChange} 
                            placeholder="Surname" 
                            required 
                        />
                        <textarea 
                            name="bio"
                            className="form-control mb-2"
                            value={formData.bio}
                            onChange={handleInputChange}
                            placeholder="Introduce Yourself"
                            rows="5"
                            style={{ resize: 'both' }}
                            required
                        ></textarea>
                        <button type="submit" className="btn btn-outline-secondary">Register</button>
                    </div>
                </form>
            </div>
        </>
    );
};

export default RegisterPage;
