import React, { useState, useEffect } from 'react';
import { getLoanPost, getPost, getImage } from '../../api/axiosConfig';
import { useParams } from 'react-router-dom';

const Dashboard = () => {
    const [postData, setPostData] = useState([]);
    const [imageData, setImageData] = useState([]);
    const { postId } = useParams();
    const [posts, setPosts] = useState([]);
    const [runCount, setRunCount] = useState(0); // New state variable to count runs

    useEffect(() => {
        const fetchSellingPosts = async () => {
            try {
                const response = await getLoanPost();
                setPosts(response.data);
            } catch (error) {
                console.error('Error fetching selling posts:', error);
                // Handle error appropriately
            }
        };

        fetchSellingPosts();
    }, []);

    useEffect(() => {
        // Log the postId of the first post (you can modify this as needed)
        console.log(posts.length > 0 ? posts[0].id : null);

        // Increment the runCount
        setRunCount((prevRunCount) => prevRunCount + 1);

        // Fetch post details based on the postId when postId changes
        getPost(postId)
            .then(response => {
                setPostData(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the post data:', error);
            });
    }, [postId, posts, runCount]); // Include runCount in the dependencies array

    const imageId = postData?.imageId;
    console.log(imageId);

    useEffect(() => {
        // Fetch image data based on imageId when imageId changes
        const fetchImage = async () => {
            try {
                const response = await getImage(imageId);
                setImageData(response.data);
            } catch (error) {
                console.error('Error fetching image:', error);
                // Handle error appropriately
            }
        };

        fetchImage();
    }, [imageId]);

    return (
        <div>
            <h1>Selling Posts</h1>
            <div className="posts-container">
                {posts.map(post => (
                    <div key={post.id} className="post">
                        <h2>{post.title}</h2>
                        <p>{post.content}</p>
                        {post.imageBase64 && (
                            <img src={`data:image/jpeg;base64,${post.imageBase64}`} alt={post.title} style={{ maxWidth: '100%' }} />
                        )}
                        {/* Additional post details here */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
