import React, { useState } from 'react';
import { createUserPost } from '../../api/axiosConfig';

const MakePost = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [postType, setPostType] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const userId = 'your-user-id'; // This should be dynamically obtained based on your app's user management

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('content', content);
    formData.append('postType', postType);
    formData.append('category', category);
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await createUserPost(userId, formData); // Update this call based on your backend's requirement
      console.log('Post created:', response.data);
      // Handle success (e.g., show success message, redirect to another page, etc.)
    } catch (error) {
      console.error('Error creating post:', error);
      // Handle error (e.g., show error message)
    }
  };

  return (
    <>
    <div className="container">

    <h1 className="mt-4 text-center">Lost Property</h1>
    <h3 className="mb-4 text-center">Add Post</h3>
    <div className="card" style={{ width: '26rem', margin: 'auto', background: 'radial-gradient(circle, rgba(238,174,202,1) 0%, rgba(212,221,232,1) 100%)' }}>

        <div className="card-body">
          <p className="card-text">Please pay attention to Bilkent rules in your definition.</p>
          <form onSubmit={handleSubmit} className="myform">
            <div className="form-group mt-2">
                    <label htmlFor="formFileLg" className="form-label"><b>Post Image</b></label>
                    <input className="form-control form-control" id="formFileLg" type="file" onChange={handleImageChange} />
            </div>

            <div className="form-group mt-2">
                <label htmlFor="postheader"><b>Post Title</b></label>
                <input id="postheader" className="form-control" type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Please enter your post header" />
            </div>

            <div className="form-group mt-2">
                <label htmlFor="postdetail"><b>Post Definition</b></label>
                <textarea className="form-control" id="postdetail" rows="3" value={content} onChange={(e) => setContent(e.target.value)} placeholder="Please enter your post details"></textarea>
            </div>
            <div className="form-group mt-2">
                <button type="submit" className="form-control btn btn-outline-success btn-block">Send Post</button>
            </div>

          </form>
        </div>
      </div>
  </div>

  <div className="fixed-bottom text-center">Bilkent University Student/Alumni Communication System - 2023</div>
  </>
  );
};

export default MakePost;
